import React, { useEffect, useState } from 'react';
import GigList from '../components/GigList';
import GigForm from '../components/GigForm';

const headerStyle = {
  background: 'linear-gradient(90deg, #1976d2 60%, #64b5f6 100%)',
  color: '#fff',
  padding: '0 30px',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'space-between',
  height: '70px',
  boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
  marginBottom: '30px',
  borderRadius: '0 0 16px 16px',
};

const navStyle = {
  display: 'flex',
  gap: '25px',
};

const navItemStyle = {
  color: '#fff',
  textDecoration: 'none',
  fontWeight: 'bold',
  fontSize: '1.1rem',
  padding: '8px 16px',
  borderRadius: '8px',
  transition: 'background 0.2s',
};

function App() {
  const [gigs, setGigs] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/gigs')
      .then(res => res.json())
      .then(data => setGigs(data));
  }, []);

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', background: '#f3f6f9', minHeight: '100vh' }}>
      <header style={headerStyle}>
        <div style={{ fontSize: '2rem', fontWeight: 'bold', letterSpacing: '1px' }}>
          Freelancer Marketplace
        </div>
        <nav style={navStyle}>
          <a href="#" style={navItemStyle}>Home</a>
          <a href="#gigs" style={navItemStyle}>Gigs</a>
          <a href="#about" style={navItemStyle}>About</a>
          <a href="#signup" style={{ ...navItemStyle, background: '#fff', color: '#1976d2' }}>Sign Up</a>
          <a href="#signin" style={{ ...navItemStyle, background: '#fff', color: '#1976d2' }}>Sign In</a>
        </nav>
      </header>
      <div style={{ maxWidth: '900px', margin: '0 auto', padding: '30px 20px' }}>
        <GigForm setGigs={setGigs} />
        <GigList gigs={gigs} />
      </div>
    </div>
  );
}

export default App;
