import React, { useState } from 'react';

function GigForm({ setGigs }) {
  const [form, setForm] = useState({ title: '', description: '', budget: '', postedBy: '' });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await fetch('http://localhost:5000/api/gigs', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form),
    });
    const newGig = await res.json();
    setGigs(prev => [...prev, newGig]);
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
      <input name="title" placeholder="Title" onChange={handleChange} /><br/>
      <textarea name="description" placeholder="Description" onChange={handleChange}></textarea><br/>
      <input name="budget" placeholder="Budget" type="number" onChange={handleChange} /><br/>
      <input name="postedBy" placeholder="Your Name" onChange={handleChange} /><br/>
      <button type="submit">Post Gig</button>
    </form>
  );
}

export default GigForm;
